mapper.update:
update task set task_index = case task_code 

		when #{item.taskCode} then #{item.taskIndex}

		else task_code end                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
		where task_code in

		#{item.taskCode}
