mapper.select:
select a.* from (select rownum as rwnum, s.* from (select board.board_code,board.title,user_tb.name,board.write_date,board.hit,board.filename,board.notice from member,board,user_tb where member.member_code=board.member_code and user_tb.id=member.id
and title like '%' || #{filed} || '%'
order by notice desc,board_code desc)s )a where rwnum between #{startRowNo} and #{endRowNo}

select board.board_code,board.title,user_tb.name,board.content,board.write_date,board.filename,user_tb.path
from member,board,user_tb
where member.member_code=board.member_code and user_tb.id=member.id and board.board_code=#{_parameter}

select board_comment.content,board_comment.write_date,user_tb.path,user_tb.name
from member,board_comment,user_tb
where member.member_code=board_comment.member_code and user_tb.id=member.id and board_comment.board_code=#{value}
